sysevent_script_action.script:
// Set the record priority to 1 - Critical
current.priority = 1;
// Update the record in the database
current.setValue('description', 'Updated by Automation script');
current.work_notes = 'Updated by Script - Priority increased to Critical - 1' + current.work_notes.getJournalEntry(1);
current.update();

